import React, { useEffect, useMemo, useRef, useState } from "react";

/**
 * Warehouse Inventory with Low-Stock Alerts
 * - Fetch stock (mock API in localStorage)
 * - Show alert when stock < threshold
 * - Auto-refresh with interval
 * - Inline stock updates (âˆ’1, +1, or set value)
 * - All-in-one single file (easy to drop into Vite React project)
 */

// ---------------- Mock API ----------------
const STORAGE_KEY = "warehouse-inventory-data-v1";

const seedData = [
  { id: "A-1001", name: "Corrugated Boxes (Small)", sku: "BOX-SM", stock: 28, threshold: 40 },
  { id: "A-1002", name: "Bubble Wrap 30cm", sku: "BUB-30", stock: 12, threshold: 20 },
  { id: "A-1003", name: "Packing Tape Clear", sku: "TAPE-CLR", stock: 95, threshold: 50 },
  { id: "A-1004", name: "Labels 100x150mm", sku: "LBL-100x150", stock: 6, threshold: 25 },
  { id: "A-1005", name: "Pallet Wrap", sku: "WRAP-PAL", stock: 0, threshold: 10 },
];

function ensureStorage() {
  if (!localStorage.getItem(STORAGE_KEY)) {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(seedData));
  }
}
function readAll() {
  ensureStorage();
  return JSON.parse(localStorage.getItem(STORAGE_KEY));
}
function writeAll(items) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(items));
}
function delay(ms) {
  return new Promise((res) => setTimeout(res, ms));
}

async function mockFetchInventory() {
  await delay(300);
  return readAll();
}
async function mockUpdateStock(id, newStock) {
  await delay(200);
  const items = readAll();
  const idx = items.findIndex((x) => x.id === id);
  if (idx !== -1) {
    items[idx].stock = Math.max(0, Number(newStock) || 0);
    writeAll(items);
  }
  return items;
}

// ---------------- Utils ----------------
function classNames(...arr) {
  return arr.filter(Boolean).join(" ");
}
function formatTime(dt) {
  return new Intl.DateTimeFormat(undefined, {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
    hour12: false,
  }).format(dt);
}

// ---------------- Main Component ----------------
export default function WarehouseInventory() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [lastUpdated, setLastUpdated] = useState(null);

  const [filter, setFilter] = useState("");
  const [onlyLow, setOnlyLow] = useState(false);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [intervalMs, setIntervalMs] = useState(10000);
  const timerRef = useRef(null);

  const [drafts, setDrafts] = useState({}); // inline edit values

  // Fetch inventory
  const fetchData = async () => {
    try {
      setError("");
      setLoading(true);
      const data = await mockFetchInventory();
      setItems(data);
      setLastUpdated(new Date());
    } catch (e) {
      console.error(e);
      setError("Failed to fetch inventory");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Auto-refresh
  useEffect(() => {
    if (!autoRefresh) {
      if (timerRef.current) clearInterval(timerRef.current);
      return;
    }
    timerRef.current = setInterval(fetchData, intervalMs);
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [autoRefresh, intervalMs]);

  const lowStockCount = useMemo(
    () => items.filter((it) => it.stock < it.threshold).length,
    [items]
  );

  const filtered = useMemo(() => {
    let list = items;
    const q = filter.trim().toLowerCase();
    if (q) {
      list = list.filter(
        (it) =>
          it.name.toLowerCase().includes(q) ||
          it.sku.toLowerCase().includes(q) ||
          it.id.toLowerCase().includes(q)
      );
    }
    if (onlyLow) list = list.filter((it) => it.stock < it.threshold);

    return [...list].sort((a, b) => {
      const aLow = a.stock < a.threshold ? 1 : 0;
      const bLow = b.stock < b.threshold ? 1 : 0;
      if (aLow !== bLow) return bLow - aLow;
      return a.name.localeCompare(b.name);
    });
  }, [items, filter, onlyLow]);

  // Update stock
  const handleAdjust = async (id, delta) => {
    const item = items.find((x) => x.id === id);
    if (!item) return;
    const next = Math.max(0, item.stock + delta);
    const data = await mockUpdateStock(id, next);
    setItems(data);
    setLastUpdated(new Date());
  };
  const handleSaveDraft = async (id) => {
    const value = drafts[id];
    if (value == null || value === "") return;
    const data = await mockUpdateStock(id, Number(value));
    setItems(data);
    setLastUpdated(new Date());
  };
  const handleChangeDraft = (id, value) => {
    setDrafts((d) => ({ ...d, [id]: value.replace(/[^0-9]/g, "") }));
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900 p-6">
      {/* Header */}
      <div className="max-w-6xl mx-auto">
        <header className="flex flex-col gap-3 md:flex-row md:items-end md:justify-between">
          <div>
            <h1 className="text-2xl md:text-3xl font-semibold tracking-tight">
              Warehouse Inventory
            </h1>
            <p className="text-sm text-gray-600">
              Low-Stock Alerts â€¢ Auto-Refresh â€¢ Inline Updates
            </p>
          </div>
          <div className="flex items-center gap-2">
            <button
              onClick={fetchData}
              className="px-3 py-2 rounded-xl bg-black text-white text-sm disabled:opacity-50 shadow"
              disabled={loading}
            >
              {loading ? "Refreshingâ€¦" : "Refresh Now"}
            </button>
            <div className="text-xs text-gray-600">
              {lastUpdated && <span>Last updated: {formatTime(lastUpdated)}</span>}
            </div>
          </div>
        </header>

        {/* Alerts */}
        <div className="mt-4">
          {lowStockCount > 0 ? (
            <div className="rounded-2xl border border-amber-300 bg-amber-50 px-4 py-3 text-amber-900">
              <strong className="font-medium">Attention:</strong>{" "}
              {lowStockCount} item{lowStockCount !== 1 ? "s" : ""} below threshold.
            </div>
          ) : (
            <div className="rounded-2xl border border-emerald-300 bg-emerald-50 px-4 py-3 text-emerald-900">
              <strong className="font-medium">All good:</strong> No low-stock items.
            </div>
          )}
        </div>

        {/* Controls */}
        <section className="mt-4 grid gap-3 md:grid-cols-3">
          <div className="col-span-2 flex items-center gap-3">
            <input
              className="w-full rounded-2xl border px-3 py-2 text-sm outline-none focus:ring-2 focus:ring-black"
              placeholder="Search by name, SKU, or IDâ€¦"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            />
            <label className="flex items-center gap-2 text-sm">
              <input
                type="checkbox"
                checked={onlyLow}
                onChange={(e) => setOnlyLow(e.target.checked)}
              />
              Only low stock
            </label>
          </div>
          <div className="flex items-center justify-between md:justify-end gap-3">
            <label className="text-sm flex items-center gap-2">
              Auto-refresh
              <input
                type="checkbox"
                checked={autoRefresh}
                onChange={(e) => setAutoRefresh(e.target.checked)}
              />
            </label>
            <label className="text-sm flex items-center gap-2">
              Every
              <select
                className="rounded-xl border px-2 py-1 text-sm"
                value={intervalMs}
                onChange={(e) => setIntervalMs(Number(e.target.value))}
              >
                <option value={5000}>5s</option>
                <option value={10000}>10s</option>
                <option value={30000}>30s</option>
                <option value={60000}>60s</option>
              </select>
            </label>
          </div>
        </section>

        {/* Table */}
        <div className="mt-4 overflow-x-auto rounded-2xl border bg-white shadow-sm">
          <table className="min-w-full text-sm">
            <thead className="bg-gray-100">
              <tr className="text-left text-gray-600">
                <th className="px-4 py-3">Item</th>
                <th className="px-4 py-3">SKU</th>
                <th className="px-4 py-3">Stock</th>
                <th className="px-4 py-3">Threshold</th>
                <th className="px-4 py-3">Status</th>
                <th className="px-4 py-3">Update</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((it) => {
                const isLow = it.stock < it.threshold;
                return (
                  <tr key={it.id} className={classNames("border-t", isLow && "bg-amber-50/60")}>
                    <td className="px-4 py-3">
                      <div className="font-medium">{it.name}</div>
                      <div className="text-xs text-gray-500">ID: {it.id}</div>
                    </td>
                    <td className="px-4 py-3">{it.sku}</td>
                    <td className="px-4 py-3 font-mono">{it.stock}</td>
                    <td className="px-4 py-3 font-mono">{it.threshold}</td>
                    <td className="px-4 py-3">
                      {isLow ? (
                        <span className="inline-flex items-center rounded-full border border-amber-300 bg-amber-100 px-2 py-0.5 text-[11px] font-semibold text-amber-900">
                          Low
                        </span>
                      ) : (
                        <span className="inline-flex items-center rounded-full border border-emerald-300 bg-emerald-100 px-2 py-0.5 text-[11px] font-semibold text-emerald-900">
                          OK
                        </span>
                      )}
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => handleAdjust(it.id, -1)}
                          className="px-2 py-1 rounded-lg border hover:bg-gray-50"
                        >
                          âˆ’1
                        </button>
                        <button
                          onClick={() => handleAdjust(it.id, +1)}
                          className="px-2 py-1 rounded-lg border hover:bg-gray-50"
                        >
                          +1
                        </button>
                        <input
                          className="w-20 rounded-lg border px-2 py-1 text-sm font-mono"
                          placeholder={`${it.stock}`}
                          value={drafts[it.id] ?? ""}
                          onChange={(e) => handleChangeDraft(it.id, e.target.value)}
                          inputMode="numeric"
                        />
                        <button
                          onClick={() => handleSaveDraft(it.id)}
                          className="px-3 py-1.5 rounded-lg bg-black text-white text-xs"
                        >
                          Save
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}

              {!loading && filtered.length === 0 && (
                <tr>
                  <td colSpan={6} className="px-4 py-12 text-center text-gray-500">
                    No items found.
                  </td>
                </tr>
              )}
              {loading && (
                <tr>
                  <td colSpan={6} className="px-4 py-12 text-center text-gray-500">
                    Loadingâ€¦
                  </td>
                </tr>
              )}
              {error && (
                <tr>
                  <td colSpan={6} className="px-4 py-3 text-center text-red-700 bg-red-50">
                    {error}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="mt-4 text-xs text-gray-500">
          Demo uses <code>localStorage</code> as a mock backend. Replace{" "}
          <code>mockFetchInventory</code> / <code>mockUpdateStock</code> with real API calls.
        </p>
      </div>
    </div>
  );
}
